mapper.update:
update tasks
taskcompanyid = #{taskcompanyid}, taskname = #{taskname}, taskdesc = #{taskdesc}, tasktype = #{tasktype}, location = #{location}, taskpayment = #{taskpayment}, taskstatus = #{taskstatus}, createtime = #{createtime}, endtime = #{endtime}, longitude = #{longitude}, latitude = #{latitude}, phone = #{phone},
where taskid = #{taskid}

mapper.select:
select count(taskid) from tasks


        and  createtime > #{startDate}


        and  createtime < #{endDate}
